!function(){tinymce.PluginManager.add('spt_tinymce_button',function(a){a.addButton('spt_tinymce_button',{title:'Insert Pricing Table',icon:'icon spt-icon',type:'menubutton',menu:[{text:'Pricing Table Wrapper',onclick:function(){a.windowManager.open({title:'Insert Pricing Table Wrapper',body:[{type:'listbox',name:'columns',label:'Columns',values:[{text:'1',value:'1'},{text:'2',value:'2'},{text:'3',value:'3'},{text:'4',value:'4'},{text:'5',value:'5'},{text:'6',value:'6'}]},{type:'listbox',name:'alignment',label:'Text alignment',values:[{text:'left',value:'left'},{text:'center',value:'center'},{text:'right',value:'right'}]}],onsubmit:function(b){a.insertContent('[pricing_table columns="'+b.data.columns+'" alignment="'+b.data.alignment+'"][/pricing_table]')}})}},{text:'Pricing Table Column',onclick:function(){a.windowManager.open({title:'Insert Pricing Table Column',body:[{type:'textbox',name:'product_id',label:'Product ID'},{type:'textbox',name:'title',label:'Column title'},{type:'textbox',name:'features',label:'Features (pipe (|) separated)',size:20},{type:'checkbox',name:'image',label:'Display product image'},{type:'checkbox',name:'highlight',label:'Highlight this column'}],onsubmit:function(b){a.insertContent('[pricing_column id="'+b.data.product_id+'" title="'+b.data.title+'" features="'+b.data.features+'" image="'+b.data.image+'" highlight="'+b.data.highlight+'"]')}})}}]})})}();